import React, { FC } from "react";
import type { INodeProps } from "./types";
import { Elements } from "../constants";
import whiteTick from "../assets/white-tick.svg";
import styles from "./styles.module.scss";

const Node: FC<INodeProps> = (props) => {
  const {
    step,
    renderNode,
    index,
    currentStepIndex,
    handleStepClick,
    showCursor,
    getStyles
  } = props;

  return (
    <div
      className={`${styles.eachNode}
      ${showCursor && styles.cursorPointer}
      ${index === currentStepIndex && styles.activeStepNode}
      ${!step.completed && currentStepIndex !== index && styles.inactiveStepNode}
      ${step.completed && currentStepIndex !== index && styles.completedStepNode}
      `}
      style={{
        ...((getStyles(Elements.Node)) || {}),
        ...((index === currentStepIndex && getStyles(Elements.ActiveNode)) || {}),
        ...((step.completed && getStyles(Elements.CompletedNode)) || {}),
        ...((!step.completed && currentStepIndex !== index
            && getStyles(Elements.InActiveNode)) || {})
      }}
      onClick={(): void | null => handleStepClick && handleStepClick()}
      role="presentation"
      id="stepper-node"
    >
      {(renderNode && renderNode(step, index))
    || (
      <>
        {step?.completed && (
          <img
            src={whiteTick}
            className={styles.whiteTickImg}
            alt=""
          />)
        || index + 1}
      </>
    )}
    </div>
  );
};

export default Node;

import { LABEL_POSITION, ORIENTATION } from "../constants";

const getLabelStyle: (orientation?: string, labelPosition?: string) => string | undefined = (orientation, labelPosition) => {
  if (orientation !== ORIENTATION.VERTICAL) {
    if (labelPosition === LABEL_POSITION.TOP) return "horizontalLabelTop";
    else if (labelPosition === LABEL_POSITION.LEFT || labelPosition === LABEL_POSITION.RIGHT)
      return ""
    else return "horizontalLabelBottom";
  } else if (labelPosition === LABEL_POSITION.RIGHT)
    return "verticalLabelRight";
};

export default getLabelStyle;

import React from "react";
import "./styles.scss";
import type { IStepInfoProps } from "./types";
import Node from "../node";
import { LABEL_POSITION, Elements, ORIENTATION } from "../constants";
import getStyles from "../utils/getStyles";
import getLabelStyle from "../utils/getLabelStyle";

const StepInfo: (props: IStepInfoProps) => JSX.Element = ({
  orientation,
  labelPosition,
  isVertical,
  isInlineLabelsAndSteps,
  index,
  currentStepIndex,
  step,
  showDescriptionsForAllSteps,
  onStepClick,
  renderNode,
  styles,
  nodeRef,
  prevConnectorClassName,
  nextConnectorClassName,
  steps
}: IStepInfoProps) => (
  <div
    id="stepper-step"
    className={
      isVertical
        ? `verticalStepperWrapper ${
          labelPosition === LABEL_POSITION.LEFT ? "labelLeft" : ""
        }`
        : "horizontalStepperWrapper"
    }
  >
    {!isInlineLabelsAndSteps && (
      <div className={getLabelStyle(orientation, labelPosition)} onClick={(): void => onStepClick && onStepClick(step, index)}>
        <div
          className="label"
          id={`step-label-${index}`}
          style={{
            ...(getStyles(styles, Elements.LabelTitle, step, index) || {}),
            ...(index === currentStepIndex &&
              (getStyles(styles, Elements.ActiveLabelTitle, step, index) ||
                {}))
          }}
        >
          {step.stepLabel}
        </div>
        {step.stepDescription && (showDescriptionsForAllSteps || index === currentStepIndex) &&
          orientation !== ORIENTATION.VERTICAL &&
          labelPosition === LABEL_POSITION.TOP && (
          <div
            className="description"
            id={`step-horizontal-top-description-${index}`}
            style={{
              ...(currentStepIndex === index
                ? getStyles(
                  styles,
                  Elements.ActiveLabelDescription,
                  step,
                  index
                ) || {}
                : getStyles(styles, Elements.LabelDescription, step, index) ||
                    {})
            }}
          >
            {step.stepDescription}
          </div>
        )}
      </div>
    )}
    <div className="stepContainer" id={`${index}-node`} ref={nodeRef}>
      <div
        className={prevConnectorClassName}
        style={{
          ...(steps[index - 1]?.completed
            ? getStyles(styles, Elements.LineSeparator, step, index) || {}
            : getStyles(styles, Elements.InactiveLineSeparator, step, index) ||
              {})
        }}
      />
      <div
        className={`node ${
          [LABEL_POSITION.TOP, LABEL_POSITION.LEFT].includes(labelPosition)
            ? "reversedNode"
            : ""
        }`}
      >
        <Node
          step={step}
          index={index}
          currentStepIndex={currentStepIndex}
          handleStepClick={(): void => onStepClick && onStepClick(step, index)}
          showCursor={!!onStepClick}
          renderNode={renderNode}
          getStyles={(element: Elements): object =>
            getStyles(styles, element, step, index)
          }
        />
      </div>
      {isInlineLabelsAndSteps && (
        <div
          className={`labelContainer ${
            [LABEL_POSITION.TOP, LABEL_POSITION.LEFT].includes(labelPosition)
              ? "reversedLabelContainer"
              : ""
          }`}
        >
          <div
            className={`label ${isVertical && "verticalStepperInlineLabel"}`}
            id={`step-inline-label-${index}`}
            style={{
              ...(getStyles(styles, Elements.LabelTitle, step, index) || {}),
              ...(index === currentStepIndex &&
                (getStyles(styles, Elements.ActiveLabelTitle, step, index) ||
                  {}))
            }}
            onClick={(): void => onStepClick && onStepClick(step, index)}
          >
            {step.stepLabel}
          </div>
        </div>
      )}
      <div
        className={nextConnectorClassName}
        style={{
          ...(step.completed
            ? getStyles(styles, Elements.LineSeparator, step, index) || {}
            : getStyles(styles, Elements.InactiveLineSeparator, step, index) ||
              {})
        }}
      />
    </div>
  </div>
);

export default StepInfo;

import React, { useRef, useEffect, useState } from "react";
import "./styles.scss";
import type { IStepProps } from "../stepper/types";
import { LABEL_POSITION, ORIENTATION } from "../constants";
import StepContent from "./stepContent";
import StepInfo from "./stepInfo";

//  Each step consists of a node, a label, and connectors to the previous and next steps.
const Step: (props: IStepProps) => JSX.Element = ({
  stepperProps,
  step,
  index
}: IStepProps) => {
  const {
    steps,
    currentStepIndex = 0,
    styles = {},
    labelPosition = LABEL_POSITION.RIGHT,
    orientation = ORIENTATION.VERTICAL,
    showDescriptionsForAllSteps = false,
    stepContent,
    onStepClick,
    renderNode
  } = stepperProps;
  const [nodeWidth, setNodeWidth] = useState(0);

  const isVertical = orientation === ORIENTATION.VERTICAL;

  /* isInlineLabelsAndSteps = true means label and steps are in the same axis (eg: Horizontal stepper with label direction left/right and
   vertical stepper with label direction top/bottom) */
  const isInlineLabelsAndSteps =
    (isVertical &&
      ![LABEL_POSITION.LEFT, LABEL_POSITION.RIGHT].includes(labelPosition)) ||
    (!isVertical &&
      [LABEL_POSITION.LEFT, LABEL_POSITION.RIGHT].includes(labelPosition));

  const nodeRef = useRef<HTMLDivElement | null>(null);

  useEffect(() => {
    const node = nodeRef.current;
    if (node) {
      const width = node.getBoundingClientRect().width;
      setNodeWidth(width);
    }
  }, [steps, labelPosition, orientation]);

  // prevConnector represents the connector line from the current step's node (nth node) to the preceding step's node (n-1 th node).
  const prevConnectorClassName = `stepConnector leftConnector ${
    steps[index - 1]?.completed ? "activeConnector" : ""
  } ${index === 0 ? "hiddenConnector" : ""}`;

  // nextConnector represents the connector line from the current step's node (nth node) to the preceding step's node (n-1 th node).

  const nextConnectorClassName = `stepConnector rightConnector ${
    steps[index]?.completed ? "activeConnector" : ""
  } ${index === steps.length - 1 ? "hiddenConnector" : ""}`;

  /* middleConnector connects the current step nextConnector to (n+1th) step prevConnector,
  allowing the display of descriptions or content between the two steps when necessary.  */

  const middleConnectorClassName = `middleStepConnector ${
    currentStepIndex > index ? "activeConnector" : ""
  } ${index === steps.length - 1 ? "hiddenConnector" : ""}`;

  return orientation !== ORIENTATION.VERTICAL &&
    labelPosition === LABEL_POSITION.TOP ? (
      <StepInfo
        orientation={orientation}
        labelPosition={labelPosition}
        isVertical={isVertical}
        isInlineLabelsAndSteps={isInlineLabelsAndSteps}
        index={index}
        currentStepIndex={currentStepIndex}
        step={step}
        showDescriptionsForAllSteps={showDescriptionsForAllSteps}
        onStepClick={onStepClick}
        renderNode={renderNode}
        styles={styles}
        nodeRef={nodeRef}
        prevConnectorClassName={prevConnectorClassName}
        nextConnectorClassName={nextConnectorClassName}
        steps={steps}
      />
    ) : (
      <div
        className={
          orientation === ORIENTATION.VERTICAL &&
        labelPosition === LABEL_POSITION.LEFT
            ? "verticalTextLeftContainer"
            : ""
        }
      >
        <StepInfo
          orientation={orientation}
          labelPosition={labelPosition}
          isVertical={isVertical}
          isInlineLabelsAndSteps={isInlineLabelsAndSteps}
          index={index}
          currentStepIndex={currentStepIndex}
          step={step}
          showDescriptionsForAllSteps={showDescriptionsForAllSteps}
          onStepClick={onStepClick}
          renderNode={renderNode}
          styles={styles}
          nodeRef={nodeRef}
          prevConnectorClassName={prevConnectorClassName}
          nextConnectorClassName={nextConnectorClassName}
          steps={steps}
        />
        <StepContent
          labelPosition={labelPosition}
          isVertical={isVertical}
          currentStepIndex={currentStepIndex}
          index={index}
          styles={styles}
          step={step}
          showDescriptionsForAllSteps={showDescriptionsForAllSteps}
          middleConnectorClassName={middleConnectorClassName}
          stepContent={stepContent}
          nodeWidth={nodeWidth}
        />
      </div>
    );
};

export default Step;

import React from "react";
import "./styles.scss";
import type { IStepperProps } from "./types";
import { ORIENTATION } from "../constants";
import Step from "./step";

const Stepper = (props: IStepperProps): JSX.Element => {
  const {
    steps,
    currentStepIndex = 0,
    orientation = ORIENTATION.VERTICAL,
    stepContent
  } = props;

  const isVertical = orientation === ORIENTATION.VERTICAL;

  return (
    <>
      <ul
        className={`stepper ${
          isVertical ? "verticalStepper" : "horizontalStepper"
        }`}
      >
        {steps.map((step, index) => <Step key={`${step.stepLabel}-${step.stepDescription}`} stepperProps={props} step={step} index={index} />)}
      </ul>
      {!isVertical && // For horizontal stepper, the content is displayed below the stepper with full width
        stepContent &&
        stepContent(steps[currentStepIndex], currentStepIndex)}
    </>
  );
};

export default Stepper;
